SELECT P.Name, P.ProductNumber, P.ProductSubcategoryID AS [P ProductSubcategoryID]	  ,PS.ProductSubcategoryID AS [PS ProductSubcategoryID]	  ,PS.Name AS [ProductSubCategory Name]  FROM Production.Product PLEFT OUTER JOIN Production.ProductSubcategory PSON P.ProductSubcategoryID = PS.ProductSubcategoryID;/* 31,465 Records */SELECT     P.FirstName   ,P.LastName   ,SOH.SalesOrderNumber   ,SOH.TotalDue AS SalesAmount   ,PPP.PhoneNumber   ,PW.PasswordHash   ,PW.PasswordSalt   ,T.Name AS TerritoryNameFROM Sales.SalesOrderHeader SOHLEFT OUTER JOIN Sales.SalesPerson SPON SP.BusinessEntityID = SOH.SalesPersonIDLEFT OUTER JOIN HumanResources.Employee EON E.BusinessEntityID = SP.BusinessEntityIDLEFT OUTER JOIN Person.Person PON P.BusinessEntityID = E.BusinessEntityIDLEFT OUTER JOIN Person.Password PWON PW.BusinessEntityID = P.BusinessEntityIDLEFT OUTER JOIN Person.PersonPhone PPPON PPP.BusinessEntityID = P.BusinessEntityIDLEFT OUTER JOIN Sales.SalesTerritory TON T.TerritoryID = SOH.TerritoryID;SELECT COUNT(*) AS Count     , SUM(SOH.TotalDue) AS SalesAmount  FROM Sales.SalesOrderHeader SOH  SELECT     P.FirstName   ,P.LastName   ,SOH.SalesOrderNumber   ,SOH.TotalDue AS SalesAmount   ,PPP.PhoneNumber   ,PW.PasswordHash   ,PW.PasswordSalt   ,T.Name AS TerritoryNameFROM Sales.SalesOrderHeader SOHLEFT OUTER JOIN Sales.SalesPerson SPON SP.BusinessEntityID = SOH.SalesPersonIDLEFT OUTER JOIN HumanResources.Employee EON E.BusinessEntityID = SP.BusinessEntityIDLEFT OUTER JOIN Person.Person PON P.BusinessEntityID = E.BusinessEntityIDLEFT OUTER JOIN Person.Password PWON PW.BusinessEntityID = P.BusinessEntityIDLEFT OUTER JOIN Person.PersonPhone PPPON PPP.BusinessEntityID = P.BusinessEntityIDLEFT OUTER JOIN Sales.SalesTerritory TON T.TerritoryID = SOH.TerritoryID WHERE T.Name = 'Northwest'ORDER BY SOH.TotalDue DESC;